                <TableBody>
                  {filteredUsers
                    .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                    .map((row) => {
                      const avatarUrl = "/static/mock-images/cities/city.jpg"

                      const { pk, jobName, jobCategory, jobPrimaryTechnology, jobLocation, jobMaxExp, jobMinExp, jobSkills, 
